mapper.select:
-- select user_id, dept_id, login_name, user_name, email, phonenumber,number,company,company_address,student_id,password, salt, user_type, status, refuse_des, create_by, create_time from sys_user select u.user_id, u.dept_id, u.login_name, u.user_name, u.email,u.number,u.employee,u.company,u.company_address, u.phonenumber,u.student_id, u.user_type,u.password, u.salt, u.status, u.refuse_des,u.create_by, u.create_time, d.dept_id, d.parent_id, d.dept_name, d.order_num, d.status as dept_status from sys_user u left join sys_dept d on u.dept_id = d.dept_id
AND u.login_name = #{searchValue}
AND u.dept_id IN (SELECT dept_id FROM sys_dept WHERE dept_id = #{deptId} OR parent_id = #{deptId})

mapper.update:
update sys_user
dept_id = #{deptId}, login_name = #{loginName}, user_name = #{userName}, email = #{email}, phonenumber = #{phonenumber}, password = #{password}, number = #{number}, student_id=#{studentId}, company=#{company}, company_address=#{companyAddress}, salt = #{salt}, status = #{status}, employee = #{employee}, refuse_des = #{refuseDes}, update_by = #{updateBy}, update_time = sysdate()
where 1=1 and user_id = #{userId}
